import React, { Component } from "react";
import { DatePicker, MuiPickersUtilsProvider } from "@material-ui/pickers";
import moment from "moment";
import { connect } from "react-redux";
import * as actions from "../actions/actions";
import DateFnsUtils from "@date-io/date-fns";
import heLocale from "date-fns/locale/he";
import enLocale from "date-fns/locale/en-US";

class MaterialDatepicker extends Component {
  constructor() {
    super();
    this.state = {
      date: moment().toDate(),
    };
  }

  handleDateChange = (date) => {
    const { property_name } = this.props;
    this.props.sendDate(property_name, moment(date));
  };
  render() {
    const { date } = this.props;
    const {language} = this.props.global
    const localeMap = {
        en: enLocale,
        he: heLocale,
      };


    return (
      <div className='material__datepicker'>
        <MuiPickersUtilsProvider utils={DateFnsUtils} locale={localeMap[language.substring(0, 2)]}>
          <div>
            <DatePicker
              format="LL"
              autoOk
              disableToolbar
              variant="inline"
              value={date ? date : moment()}
              onChange={this.handleDateChange}
            />
          </div>
        </MuiPickersUtilsProvider>
      </div>
    );
  }
}

function mapStateToProps({  global }) {
    return {  global };
  }

  export default connect(mapStateToProps, actions)(MaterialDatepicker);
